import { offersByCafe } from "@/lib/offers";
import { redirect } from "next/navigation";
import Image from "next/image";
import { Icon } from "@iconify/react/dist/iconify.js";
import SubmitButton from "@/components/SubmitButton";

import { Lora } from "next/font/google";

const lora = Lora({
  subsets: ["latin"],
  weight: ["400", "500", "600", "700"], // pick what you need
  style: ["normal", "italic"], // optional
  display: "swap", // good CLS behavior
});

export default function CafePage({
  params,
  searchParams,
}: {
  params: { cafeSlug: string };
  searchParams: { error?: string };
}) {
  const cfg = offersByCafe[params.cafeSlug];
  if (!cfg) return <div className="p-6">Unknown café.</div>;

  async function claim(formData: FormData) {
    "use server";
    const email = String(formData.get("email") || "").trim();
    const cafeSlug = params.cafeSlug;

    const base = process.env.NEXT_PUBLIC_BASE_URL ?? "http://localhost:3000";

    const res = await fetch(`${base}/api/vouchers`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      // you could also validate email here before fetch
      body: JSON.stringify({ cafeSlug, email }),
      cache: "no-store",
    });

    const data = await res.json();

    if (res.ok && data.viewUrl) {
      redirect(data.viewUrl); // ✅ type-safe: action returns void/never
    }

    // Redirect back with an error message
    const err = data?.error || "Failed to create voucher — please try again.";
    redirect(`/${cafeSlug}?error=${encodeURIComponent(err)}`);
  }

  return (
    <div className="max-w-md mx-auto p-6 space-y-4">
      <div className="container flex flex-col items-center">
        <div className="w-full flex items-center justify-center mb-10">
          <KasaCafeLogo className="w-12 h-12 text-foreground" />
          <div>x</div>
          <CCLogo
            className="w-16 h-16 text-foreground"
            darkColour="var(--foreground)"
          />
        </div>
        <h1
          className={`text-4xl font-semibold text-center uppercase ${lora.className} mb-5`}
        >
          {cfg.offerName}
        </h1>
        <div
          style={{
            background: `linear-gradient(to right, #d68c7a 0%, #d68c7a 60%, #2f211a33)`,
          }}
          className=" rounded-2xl my-2 max-w-80 w-full relative bg-gradient-to-r from-[var(--mainBrown)] from-0% via-[var(--mainBrown)] via-70% to-[#2f211a33] sm:mr-4 gap-y-5 flex flex-col"
        >
          <div className="flex justify-between  px-4 pt-4">
            <div className="flex items-center space-x-2">
              <Image
                src={"/kasalogo1.webp"}
                alt={cfg.offerName + " logo"}
                width={35}
                height={35}
                className="object-cover rounded-full w-[35px] h-[35px]"
              />
              <div className="text-[#fff]">
                <h1 className="text-base ">{cfg.name}</h1>
                <div className="flex items-center">
                  <Icon
                    icon="hugeicons:location-01"
                    className="h-3 text-[var(--backgroundColour)]  "
                  />
                  <p className="text-xs">Fitsrovia</p>
                </div>
              </div>
            </div>
            <div>
              <div className="text-xs px-3 py-[1px] max-h-5 rounded-full bg-[#d68c7a]/80 text-center justify-center items-center flex text-white">
                Valid till 07/10/25
              </div>
              <img
                style={{
                  width: "50%",
                  height: "100%",
                  position: "absolute",
                  right: "0px",
                  top: "0px",
                  zIndex: -1,
                  borderRadius: "20px 20px 20px 20px",
                }}
                src="/kasabackground.jpg"
                alt=""
                className="object-cover"
              />
            </div>
          </div>
          <ul className="list-disc pl-6 flex gap-x-2 pb-4">
            {cfg.included.map((i) => (
              <li
                key={i.name}
                className="bg-white/80 h-5 flex items-center justify-between px-1.5 rounded-full text-xs truncate"
              >
                {i.number} × {i.name}
              </li>
            ))}
          </ul>
        </div>

        {/* Optional error banner from previous redirect */}
        {searchParams?.error && (
          <p className="p-2 rounded bg-red-100 text-red-800">
            {searchParams.error}
          </p>
        )}

        <div className="mt-6 space-y-3 w-full max-w-80 mb-6">
          <p className="text-sm text-foreground/80">Follow us on insta:</p>

          <a
            href="https://instagram.com/kasacafelondon"
            target="_blank"
            rel="noopener noreferrer"
            aria-label="Open @kasacafelondon on Instagram"
            className="group flex w-full items-center justify-between rounded-xl border border-gray-300 bg-white px-4 py-3 shadow-sm transition hover:bg-gray-50 hover:shadow-md active:scale-[.99]"
          >
            <span className="flex items-center gap-3">
              {/* Instagram glyph (monochrome) */}
              <svg
                viewBox="0 0 24 24"
                className="h-5 w-5 text-foreground"
                aria-hidden
              >
                <path
                  fill="currentColor"
                  d="M7 2h10a5 5 0 015 5v10a5 5 0 01-5 5H7a5 5 0 01-5-5V7a5 5 0 015-5zm10 2H7a3 3 0 00-3 3v10a3 3 0 003 3h10a3 3 0 003-3V7a3 3 0 00-3-3zm-5 3a5 5 0 110 10 5 5 0 010-10zm0 2.2a2.8 2.8 0 100 5.6 2.8 2.8 0 000-5.6zM18.6 6.4a1.1 1.1 0 11-2.2 0 1.1 1.1 0 012.2 0z"
                />
              </svg>
              <span className="font-medium text-foreground">@kasacafelondon</span>
            </span>
            <span className="text-xs text-foreground/50 group-hover:text-foreground/70">
              Open
            </span>
          </a>

          <a
            href="https://instagram.com/coffeeculture_uk"
            target="_blank"
            rel="noopener noreferrer"
            aria-label="Open @coffeeculture_uk on Instagram"
            className="group flex w-full items-center justify-between rounded-xl border border-gray-300 bg-white px-4 py-3 shadow-sm transition hover:bg-gray-50 hover:shadow-md active:scale-[.99]"
          >
            <span className="flex items-center gap-3">
              <svg
                viewBox="0 0 24 24"
                className="h-5 w-5 text-foreground"
                aria-hidden
              >
                <path
                  fill="currentColor"
                  d="M7 2h10a5 5 0 015 5v10a5 5 0 01-5 5H7a5 5 0 01-5-5V7a5 5 0 015-5zm10 2H7a3 3 0 00-3 3v10a3 3 0 003 3h10a3 3 0 003-3V7a3 3 0 00-3-3zm-5 3a5 5 0 110 10 5 5 0 010-10zm0 2.2a2.8 2.8 0 100 5.6 2.8 2.8 0 000-5.6zM18.6 6.4a1.1 1.1 0 11-2.2 0 1.1 1.1 0 012.2 0z"
                />
              </svg>
              <span className="font-medium text-foreground">@coffeeculture_uk</span>
            </span>
            <span className="text-xs text-foreground/50 group-hover:text-foreground/70">
              Open
            </span>
          </a>
        </div>

        <form action={claim}>
          <label className="block mb-2 text-sm">Email</label>
          <input
            name="email"
            type="email"
            required
            className="border p-2 w-full rounded"
            placeholder="you@email.com"
          />
          <SubmitButton>Get voucher</SubmitButton>
        </form>
      </div>
    </div>
  );
}

